import io.ktor.server.application.*
import io.ktor.server.request.*
import io.ktor.server.response.*
import io.ktor.server.routing.*
import io.ktor.http.*

fun Route.bookRoutes() {
    // Create Book
    post("/books") {
        val requestBook = call.receive<Book>() // expects id, but we'll override it below

        // Generate unique ID
        val book = synchronized(lock) {
            val newBook = requestBook.copy(id = nextId++)
            books.add(newBook)
            newBook
        }
        call.respond(HttpStatusCode.Created, book)
    }

    // Read All Books
    get("/books") {
        call.respond(books)
    }

    // Read Single Book
    get("/books/{id}") {
        val id = call.parameters["id"]?.toIntOrNull()
        if (id == null) {
            call.respond(HttpStatusCode.BadRequest, "Invalid id")
            return@get
        }
        val book = books.find { it.id == id }
        if (book != null) {
            call.respond(book)
        } else {
            call.respond(HttpStatusCode.NotFound, "Book not found")
        }
    }

    // Update Book
    put("/books/{id}") {
        val id = call.parameters["id"]?.toIntOrNull()
        if (id == null) {
            call.respond(HttpStatusCode.BadRequest, "Invalid id")
            return@put
        }
        val updatedBook = call.receive<Book>()
        synchronized(lock) {
            val index = books.indexOfFirst { it.id == id }
            if (index == -1) {
                call.respond(HttpStatusCode.NotFound, "Book not found")
            } else {
                val bookToSave = updatedBook.copy(id = id)
                books[index] = bookToSave
                call.respond(bookToSave)
            }
        }
    }

    // Delete Book
    delete("/books/{id}") {
        val id = call.parameters["id"]?.toIntOrNull()
        if (id == null) {
            call.respond(HttpStatusCode.BadRequest, "Invalid id")
            return@delete
        }
        synchronized(lock) {
            val removed = books.removeIf { it.id == id }
            if (removed) {
                call.respond(HttpStatusCode.NoContent)
            } else {
                call.respond(HttpStatusCode.NotFound, "Book not found")
            }
        }
    }
}
```

